var SignalValidator = require('..');

	var definition = {
    	type: 'prontohex',
    	carrier: 38000, 
		repetitions: 10,
	    cmds: {
    	    "Test": "0000 0073 0000 000C 0020 0020 0040 0020 0020 0020 0020 0020 0020 0020 0020 0020 0020 0040 0020 0020 0020 0020 0020 0020 0040 0040 0020 0CA4"
	    },
		modulation: {
			type: 'GFSK',
			baudRate: 100000,
			channelSpacing: 100000,
			channelDeviation: 50000,
		}
	}

var opts = {
	frequency: 'ir'
}

try {
	var signal = new SignalValidator(definition, opts);

	var validResult = signal.valid();
	if( validResult === true ) {
		console.log('valid signal!');
	} else {
		console.log('invalid signal!');
		console.log(JSON.stringify(validResult, false, 4));
	}

} catch(e){
	console.error(e.stack);
}
